import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { SimpleSelect } from '@instructure/ui-simple-select'
import { IconMicLine } from '@instructure/ui-icons'
import themeable from '@instructure/ui-themeable'
import DeviceSelection from '../DeviceSelection'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import * as CaptureActions from '../../../actions'
import { IconMicOffLine } from '../IconMicOffLine'

import { translate } from '../../../constants/translated/translations'

const MICROPHONE_DISABLED = 'microphone_disabled'

/**
---
private: true
---
**/
/* eslint-disable react/prop-types */
@themeable({})
class AudioDeviceSelection extends Component {
  static propTypes = {
    isDisabled: PropTypes.bool,
  }

  static defaultProps = {
    isDisabled: false
  }

  formatLabel = DeviceSelection.formatLabel
  renderOptions = DeviceSelection.renderOptions
  icon = <IconMicLine inline={false} />
  disabledIcon = <IconMicOffLine inline={false} />

  onDeviceSelect = (_, { id }) => {
    if (!id) return

    if (this.isDeviceDisabled(id)) {
      this.props.actions.audioDeviceDisabled()
      return
    }

    this.props.actions.audioDeviceChanged(id)
  }

  isDeviceDisabled (id = this.getSelectedDeviceId()) {
    return id === MICROPHONE_DISABLED
  }

  getSelectedDeviceId () {
    if (this.props.audioDeviceId === '') {
      return MICROPHONE_DISABLED
    }

    return this.props.audioDeviceId
  }

  getSelectedIcon () {
    return this.isDeviceDisabled() ? this.disabledIcon : this.icon
  }

  getInteractionType () {
    return this.props.isDisabled || this.props.devices.audioinput.every(audioDevice => audioDevice.deviceId === '') ? 'disabled' : 'enabled'
  }

  render () {
    return (
      <SimpleSelect
        renderLabel={() => ``}
        value={this.getSelectedDeviceId()}
        onChange={this.onDeviceSelect}
        renderBeforeInput={this.getSelectedIcon()}
        interaction={this.getInteractionType()}
        data-test="AudioDeviceSelection"
      >
        {this.renderOptions(this.props.devices.audioinput, this.icon, MICROPHONE_DISABLED, translate('MICROPHONE_DISABLED'), this.disabledIcon)}
      </SimpleSelect>
    )
  }
}

const mapStateToProps = state => ({ audioDeviceId: state.audioDeviceId, devices: state.devices })

const mapDispatchToProps = dispatch => ({
  actions: bindActionCreators(CaptureActions, dispatch)
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(AudioDeviceSelection)
