import { expect } from 'chai';
import React from 'react';
import { mount } from 'enzyme';
import createMockStore from '../../test/createMockStore';
import OutcomeList from '../OutcomeList';
describe('OutcomeList', function () {
  function makeProps() {
    var store = createMockStore();
    return {
      store: store,
      artifactType: 'foo',
      artifactId: '1',
      host: 'http://foo.outcomes.foo',
      jwt: 'secretfoo'
    };
  }

  it('renders', function () {
    var wrapper = mount(React.createElement("div", {
      id: "app"
    }, React.createElement(OutcomeList, makeProps())));
    expect(wrapper.find('OutcomeLabels')).to.have.length(1);
  });
  it('sets the scope', function () {
    var props = makeProps();
    var wrapper = mount(React.createElement("div", {
      id: "app"
    }, React.createElement(OutcomeList, props)));
    expect(wrapper.find('OutcomeLabels').prop('scope')).to.equal('foo:::1');
  });
});