import { expect } from 'chai';
import { List, fromJS } from 'immutable';
import { getFeatures } from '../selectors';
describe('context/selectors', function () {
  var state = fromJS({
    features: ['feature_1', 'feature_2']
  });
  describe('getFeatures', function () {
    it('retrieves the list of features', function () {
      expect(getFeatures(state)).to.deep.equal(['feature_1', 'feature_2']);
    });
    it('returns [] when empty', function () {
      var newState = state.setIn(['features'], List());
      expect(getFeatures(newState)).to.deep.equal([]);
    });
    it('returns [] when unset', function () {
      var newState = state.deleteIn(['features']);
      expect(getFeatures(newState)).to.deep.equal([]);
    });
  });
});