import { Map } from 'immutable';
import { expect } from 'chai';
import reducerFactory from '../reducers';
describe('config/reducers', function () {
  describe('config', function () {
    it('remains constant', function () {
      var reducer = reducerFactory({
        host: 'myhost',
        jwt: 'myjwt'
      });
      var newState = reducer(Map(), {
        type: 'foo',
        payload: 'bar'
      });
      expect(newState.get('host')).to.equal('myhost');
      expect(newState.get('jwt')).to.equal('myjwt');
    });
  });
});