import { expect } from 'chai';
import React from 'react';
import { Provider } from 'react-redux';
import { mount } from 'enzyme';
import { fromJS } from 'immutable';
import createMockStore from '../../test/createMockStore';
import ConnectedAlignmentLabels from '../ConnectedAlignmentLabels';
describe('ConnectedAlignmentLabels', function () {
  it('renders', function () {
    var store = createMockStore(fromJS({
      scopeForTest: {
        alignments: {
          alignedOutcomes: [{
            id: 1,
            label: 'foo',
            title: 'baz'
          }, {
            id: 2,
            label: 'bar',
            title: 'woz'
          }]
        }
      }
    }));
    var wrapper = mount(React.createElement(Provider, {
      store: store
    }, React.createElement(ConnectedAlignmentLabels, {
      scope: "scopeForTest"
    })));
    expect(wrapper.find('OutcomeLabels')).to.have.length(1);
    expect(wrapper.text()).to.match(/baz,woz/);
  });
});