import { expect } from 'chai';
import { SET_FEATURES } from '../../../constants';
import reducer from '../reducers';
describe('features/reducers', function () {
  var state = [];

  var reduce = function reduce(state, type, payload) {
    return reducer(state, {
      type: type,
      payload: payload
    });
  };

  context('features', function () {
    it('updates features list', function () {
      var features = ['feature_1', 'feature_2'];
      var newState = reduce(state, SET_FEATURES, features);
      expect(newState.toJS()).to.deep.equal(['feature_1', 'feature_2']);
    });
  });
});