import { expect } from 'chai';
import { fromJS } from 'immutable';
import { getOutcomeList, getListPage, getListTotal } from '../selectors';
describe('OutcomeTray/selectors', function () {
  var scope = 'scopeForTest';
  var state = fromJS({
    scopeForTest: {
      config: {
        contextUuid: 'course_100'
      },
      OutcomePicker: {
        tray: {
          list: [],
          pagination: {
            page: 7,
            total: 100
          }
        }
      }
    }
  });
  describe('getOutcomeList', function () {
    it('returns the outcome list', function () {
      expect(getOutcomeList(state, scope)).to.deep.equal([]);
    });
  });
  describe('getListPage', function () {
    it('returns the current page', function () {
      expect(getListPage(state, scope)).to.equal(7);
    });
  });
  describe('getListTotal', function () {
    it('returns the current total', function () {
      expect(getListTotal(state, scope)).to.equal(100);
    });
  });
});