import { expect } from 'chai';
import { SET_SCOPE } from '../../../constants';
import reducer from '../reducers';
describe('activePicker/reducers', function () {
  var state = 'begin_scope';

  var reduce = function reduce(state, type, payload) {
    return reducer(state, {
      type: type,
      payload: payload
    });
  };

  it('SET_SCOPE updates the scope', function () {
    var scope = 'current_scope';
    var newState = reduce(state, SET_SCOPE, scope);
    expect(newState).to.deep.equal('current_scope');
  });
});