import { expect } from 'chai';
import { fromJS, Map } from 'immutable';
import { getConfig } from '../selectors';
describe('context/selectors', function () {
  var state = fromJS({
    scopeForTest: {
      config: {
        foo: 'bar'
      }
    }
  });
  describe('getConfig', function () {
    it('retrieves the correct value', function () {
      expect(getConfig(state, 'scopeForTest')).to.deep.equal({
        foo: 'bar'
      });
    });
    it('returns an empty object if no config present', function () {
      expect(getConfig(Map(), 'scopeForTest')).to.deep.equal({});
    });
  });
});