import { expect } from 'chai';
import React from 'react';
import PropTypes from 'prop-types';
import { mount } from 'enzyme';
import createMockStore from '../../test/createMockStore';
import OutcomeSDKComponent from '../OutcomeSDKComponent';
import { getConfig } from '../../store/config/selectors';
describe('OutcomeSDKComponent', function () {
  var Inner = function Inner(props) {
    return React.createElement("div", null);
  };

  var Wrapped = OutcomeSDKComponent(Inner, {
    foo: PropTypes.string
  });

  function makeProps() {
    var props = arguments.length > 0 && arguments[0] !== void 0 ? arguments[0] : {};
    var store = createMockStore();
    return Object.assign({
      store: store,
      artifactType: 'foo',
      artifactId: '1',
      host: 'http://foo.outcomes.foo',
      jwt: 'secretfoo'
    }, props);
  }

  it('requires sdk prop types', function () {
    expect(Wrapped.propTypes.artifactType).to.equal(PropTypes.string.isRequired);
    expect(Wrapped.propTypes.artifactId).to.equal(PropTypes.string.isRequired);
    expect(Wrapped.propTypes.host).to.equal(PropTypes.string.isRequired);
    expect(Wrapped.propTypes.jwt).to.equal(PropTypes.string.isRequired);
  });
  it('adds required prop types', function () {
    expect(Wrapped.propTypes.foo).to.equal(PropTypes.string);
  });
  it('renders', function () {
    var wrapper = mount(React.createElement("div", {
      id: "app"
    }, React.createElement(Wrapped, makeProps())));
    expect(wrapper.find(Inner)).to.have.length(1);
  });
  it('sets the scope', function () {
    var props = makeProps();
    var wrapper = mount(React.createElement("div", {
      id: "app"
    }, React.createElement(Wrapped, props)));
    expect(wrapper.find(Inner).prop('scope')).to.equal('foo:::1');
  });
  it('creates a user scope', function () {
    var props = makeProps({
      userUuid: 'myUserId'
    });
    var wrapper = mount(React.createElement("div", {
      id: "app"
    }, React.createElement(Wrapped, props)));
    expect(wrapper.find(Inner).prop('scope')).to.equal('user::foo:1:myUserId');
  });
  it('uses the supplied store', function () {
    var props = makeProps();
    var wrapper = mount(React.createElement(Wrapped, props));
    expect(wrapper.instance().store()).to.equal(props.store);
  });
  it('creates a default store', function () {
    var props = makeProps({
      store: null
    });
    var wrapper = mount(React.createElement(Wrapped, props));
    expect(wrapper.instance().store()).not.to.be.null;
  });
  it('converts null context uuid to empty string property', function () {
    var props = makeProps({
      contextUuid: null
    });
    var wrapper = mount(React.createElement(Wrapped, props));
    expect(wrapper.find(Inner).prop('contextUuid')).to.equal('');
  });
  it('converts null context uuid to empty store key', function () {
    var props = makeProps({
      store: null,
      contextUuid: null
    });
    var wrapper = mount(React.createElement(Wrapped, props));
    expect(getConfig(wrapper.instance().store().getState(), 'foo:::1').contextUuid).to.equal('');
  });
});