import { expect } from 'chai';
import { fromJS } from 'immutable';
import * as actions from '../actions';
import reducer from '../reducers';
describe('OutcomeTray/reducers', function () {
  var state = fromJS({
    entries: [],
    pagination: {
      page: 1,
      total: null
    }
  });
  describe('list', function () {
    it('is set by setOutcomeList', function () {
      var next = reducer(state, actions.setOutcomeList(['1', '2']));
      expect(next.get('list')).to.deep.equal(fromJS(['1', '2']));
    });
  });
  describe('page', function () {
    it('is set by setListPage', function () {
      var next = reducer(state, actions.setListPage(111));
      expect(next.get('pagination').get('page')).to.equal(111);
    });
  });
  describe('total', function () {
    it('is set by setListTotal', function () {
      var next = reducer(state, actions.setListTotal(9));
      expect(next.get('pagination').get('total')).to.equal(9);
    });
  });
});