import { expect } from 'chai';
import React from 'react';
import { Provider } from 'react-redux';
import { mount } from 'enzyme';
import { fromJS } from 'immutable';
import createMockStore from '../../test/createMockStore';
import ConnectedOutcomeTray from '../ConnectedOutcomeTray';
describe('ConnectedOutcomeTray', function () {
  it('renders', function () {
    var store = createMockStore(fromJS({
      scopeForTest: {
        OutcomePicker: {
          state: 'loading',
          search: {
            searchText: 'testing',
            isLoading: false,
            entries: []
          },
          scope: 'scopeForTest',
          focusedOutcome: null
        }
      }
    })); //

    var wrapper = mount(React.createElement(Provider, {
      store: store
    }, React.createElement(ConnectedOutcomeTray, {
      scope: "scopeForTest"
    })));
    expect(wrapper.find('OutcomeTray')).to.have.length(1);
  });
});