import { expect } from 'chai';
import React from 'react';
import { Provider } from 'react-redux';
import { mount } from 'enzyme';
import { fromJS } from 'immutable';
import createMockStore from '../../test/createMockStore';
import ConnectedOutcomePickerModal from '../ConnectedOutcomePickerModal';
describe('ConnectedOutcomePickerModal', function () {
  it('renders', function () {
    var store = createMockStore(fromJS({
      OutcomePicker: {
        state: 'loading',
        scope: 'scopeForTest'
      }
    }));
    var wrapper = mount(React.createElement(Provider, {
      store: store
    }, React.createElement(ConnectedOutcomePickerModal, {
      outcomePicker: React.createElement("div", null),
      scope: "scopeForTest"
    })));
    expect(wrapper.find('OutcomePickerModal')).to.have.length(1);
  });
});