import { expect } from 'chai';
import { fromJS } from 'immutable';
import { getScope, isOpen } from '../selectors';
describe('activePicker/selectors', function () {
  var state = fromJS({
    activePicker: 'current_scope'
  });
  describe('getScope', function () {
    it('retrieves the current scope', function () {
      expect(getScope(state)).to.equal('current_scope');
    });
    it('returns `` when empty', function () {
      var newState = state.setIn(['activePicker'], '');
      expect(getScope(newState)).to.equal('');
    });
  });
  describe('isOpen', function () {
    it('returns true if the scope matches', function () {
      expect(isOpen(state, 'current_scope')).to.equal(true);
    });
    it('returns false if the scope doesnt match', function () {
      expect(isOpen(state, '')).to.equal(false);
    });
  });
});