import * as React from 'react';

export type CodeEditorLanguage = "sh" | "js" | "json" | "javascript" | "jsx" | "shell" | "css" | "html" | "markdown" | "yaml" | "yml" | "bash";

export type CodeEditorAttachment = "bottom" | "top";

export interface CodeEditorProps {
    label: string;
    language?: CodeEditorLanguage;
    readOnly?: boolean;
    onChange?: (...args: any[])=>any;
    options?: Object;
    attachment?: CodeEditorAttachment;
    /**
     * the selected value (when controlled via the `onChange` prop)
     */
    value?: string;
}

export class CodeEditor extends React.Component<CodeEditorProps, any> {
    render(): JSX.Element;

}

