import * as React from 'react';

export type HeadingBorder = "none" | "top" | "bottom";

export type HeadingColor = "primary" | "secondary" | "primary-inverse" | "secondary-inverse" | "inherit";

export type HeadingLevel = "h1" | "h2" | "h3" | "h4" | "h5" | "reset";

export interface HeadingProps {
    /**
     * Add a top- or bottom-border to the Heading
     */
    border?: HeadingBorder;
    /**
     * The text content of the Heading
     */
    children?: any;
    /**
     * The font color to render
     */
    color?: HeadingColor;
    /**
     * The *visual* appearance of the Heading: h1 is largest; h5 is smallest.
     */
    level?: HeadingLevel;
    /**
     * Choose the element Heading should render as. Will default to the `level` prop
     * if not specified.
     */
    as?: any;
    margin?: any;
    /**
     * Provides a ref to the underlying HTML element
     */
    elementRef?: (...args: any[])=>any;
    /**
     * eslint-disable react/require-default-props
     */
    ellipsis?: boolean;
}

export class Heading extends React.Component<HeadingProps, any> {
    render(): JSX.Element;

}

