import * as React from 'react';

export interface ApplyLocaleProps {
    /**
     * A standard language id
     */
    locale?: string;
    /**
     * A timezone identifier in the format: Area/Location
     */
    timezone?: string;
    /**
     * accepts only one child (children must be wrapped in a single component/element)
     */
    children?: React.ReactNode;
}

export class ApplyLocale extends React.Component<ApplyLocaleProps, any> {
    render(): JSX.Element;

}




export type ApplyTextDirectionDir = "ltr" | "rtl";

export type ApplyTextDirectionChildren = React.ReactNode | ((...args: any[])=>any);

export interface ApplyTextDirectionProps {
    /**
     * string 'ltr' or 'rtl' representing the document direction
     */
    dir?: ApplyTextDirectionDir;
    /**
     * a single child (children must be wrapped in a single component/element) or function
     * returning a child called with the following arguments:
     * @param {string} dir - the string value 'rtl' or 'ltr'
     * @param {Boolean} rtl - boolean value true if the direction is 'rtl'
     */
    children?: ApplyTextDirectionChildren;
    /**
     * accepts only one child (children must be wrapped in a single component/element)
     */
    as?: any;
}

export class ApplyTextDirection extends React.Component<ApplyTextDirectionProps, any> {
    render(): JSX.Element;

}

