import * as React from 'react';

export type InlineListAs = "ul" | "ol";

export type InlineListSize = "small" | "medium" | "large";

export type InlineListDelimiter = "none" | "pipe" | "slash" | "arrow";

export type InlineListItemSpacing = "none" | "xxx-small" | "xx-small" | "x-small" | "small" | "medium" | "large" | "x-large" | "xx-large";

export interface InlineListProps {
    /**
     * Only accepts `<InlineList.Item>` as a child
     */
    children?: any;
    as?: InlineListAs;
    /**
     * Valid values are `0`, `none`, `auto`, `xxx-small`, `xx-small`, `x-small`,
     * `small`, `medium`, `large`, `x-large`, `xx-large`. Apply these values via
     * familiar CSS-like shorthand. For example: `margin="small auto large"`.
     */
    margin?: any;
    size?: InlineListSize;
    delimiter?: InlineListDelimiter;
    /**
     * Sets the margin separating each ListItem.
     */
    itemSpacing?: InlineListItemSpacing;
    elementRef?: (...args: any[])=>any;
}

export class InlineList extends React.Component<InlineListProps, any> {
    render(): JSX.Element;

}




export type ListAs = "ul" | "ol";

export type ListSize = "small" | "medium" | "large";

export type ListItemSpacing = "none" | "xxx-small" | "xx-small" | "x-small" | "small" | "medium" | "large" | "x-large" | "xx-large";

export type ListVariant = "default" | "unstyled" | "inline";

export interface ListProps {
    /**
     * Only accepts `<List.Item>` as a child
     */
    children?: any;
    as?: ListAs;
    /**
     * One of: none, dashed, solid
     */
    delimiter?: any;
    /**
     * When set, renders the List Items without a list style type.
     */
    isUnstyled?: boolean;
    /**
     * Valid values are `0`, `none`, `auto`, `xxx-small`, `xx-small`, `x-small`,
     * `small`, `medium`, `large`, `x-large`, `xx-large`. Apply these values via
     * familiar CSS-like shorthand. For example: `margin="small auto large"`.
     */
    margin?: any;
    size?: ListSize;
    /**
     * Sets the margin separating each ListItem.
     */
    itemSpacing?: ListItemSpacing;
    elementRef?: (...args: any[])=>any;
    /**
     * __deprecated__ Option will be to use the isUnstyled prop or the InlineList component
     */
    variant?: ListVariant;
}

export class List extends React.Component<ListProps, any> {
    render(): JSX.Element;

}

