import React, { Component } from 'react'
import PropTypes from 'prop-types'

import { pickProps } from '@instructure/ui-react-utils'
import { Menu, MenuItemGroup, MenuItem } from '@instructure/ui-menu'
import { Text } from '@instructure/ui-text'
import { ScreenReaderContent } from '@instructure/ui-a11y-content'

import PlayerButton from '../../../PlayerButton'
import { translate } from '../../../../constants/translated/translations'

/**
---
private: true
---
**/
class PlaybackSpeed extends Component {
  static propTypes = {
    showPopover: PropTypes.bool.isRequired,
    togglePopover: PropTypes.func.isRequired,
    videoId: PropTypes.string.isRequired,
    mountNode: PropTypes.func.isRequired,
    playbackSpeed: PropTypes.number.isRequired,
    playbackSpeedOptions: PropTypes.arrayOf(PropTypes.number).isRequired,
    handleKeyDown: PropTypes.func.isRequired,
    handleOnSelect: PropTypes.func.isRequired,
    handleOnMouseMove: PropTypes.func.isRequired,
    forwardRef: PropTypes.func
  }

  static defaultProps = {
    forwardRef: (ref) => {}
  }

  getPlaybackSpeedText = playbackSpeed => `${playbackSpeed}x`

  renderPlaybackSpeedOptionLabels = playbackSpeedOptions =>
    playbackSpeedOptions.map(playbackSpeed => (
      <MenuItem
        key={playbackSpeed.toString()}
        value={playbackSpeed}
        onKeyDown={this.props.handleKeyDown}
      >
        {this.getPlaybackSpeedText(playbackSpeed)}
      </MenuItem>
    ))

  render() {
    const {
      showPopover,
      togglePopover,
      videoId,
      playbackSpeed,
      mountNode,
      handleOnSelect,
      handleOnMouseMove,
      playbackSpeedOptions
    } = this.props
    const label = translate('PLAYBACK_SPEED')
    const playbackSpeedText = this.getPlaybackSpeedText(playbackSpeed)
    const screenReaderContent = (
      <ScreenReaderContent>{label} {playbackSpeedText}</ScreenReaderContent>
    )

    if (!playbackSpeedOptions || !playbackSpeedOptions.length) {
      return null
    }

    return (
      <Menu
        placement="top"
        show={showPopover}
        onToggle={togglePopover}
        trigger={
          <PlayerButton
            {...pickProps(this.props, PlayerButton.propTypes)}
            tooltipLabel={label}
            tooltipMountNode={mountNode}
            videoId={videoId}
          >
            {screenReaderContent}
            <Text size={playbackSpeedText.length >= 5 ? 'x-small' : 'medium'}>
              {playbackSpeedText}
            </Text>
          </PlayerButton>
        }
        mountNode={mountNode()}
        label={label}
      >
        <MenuItemGroup
          onMouseMove={handleOnMouseMove}
          label={screenReaderContent}
          selected={[playbackSpeed]}
          onSelect={handleOnSelect}
        >
          {this.renderPlaybackSpeedOptionLabels(playbackSpeedOptions)}
        </MenuItemGroup>
      </Menu>
    )
  }
}

export default PlaybackSpeed
