import React, { Component } from 'react'
import PropTypes from 'prop-types'

import { pickProps } from '@instructure/ui-react-utils'
import { Menu, MenuItemGroup, MenuItem } from '@instructure/ui-menu'
import { IconSettingsSolid } from '@instructure/ui-icons'
import { ScreenReaderContent } from '@instructure/ui-a11y-content'

import { sourcesType } from '../../../Player/PropTypes'
import PlayerButton from '../../../PlayerButton'
import { translate } from '../../../../constants/translated/translations'

/**
---
private: true
---
**/
class SourceChooser extends Component {
  static propTypes = {
    showPopover: PropTypes.bool.isRequired,
    togglePopover: PropTypes.func.isRequired,
    videoId: PropTypes.string.isRequired,
    mountNode: PropTypes.func.isRequired,
    selectedSrc: PropTypes.string,
    sources: sourcesType,
    handleKeyDown: PropTypes.func.isRequired,
    handleOnSelect: PropTypes.func.isRequired,
    handleOnMouseMove: PropTypes.func.isRequired,
    forwardRef: PropTypes.func
  }

  static defaultProps = {
    forwardRef: (ref) => {}
  }

  state = {
    showPopover: false
  }

  renderSourcesLabels = sources => {
    if (typeof sources[0] === 'string') {
      return (
        sources.map((src, idx) => (
          <MenuItem key={src} value={src} onKeyDown={this.props.handleKeyDown}>
            Source{idx}
          </MenuItem>
        ))
      )
    }

    return (
      sources.map(source => {
        const { src, label } = source

        return (
          <MenuItem key={src} value={src} onKeyDown={this.props.handleKeyDown}>
            {label}
          </MenuItem>
        )
      })
    )
  }

  render() {
    const {
      showPopover,
      togglePopover,
      videoId,
      mountNode,
      selectedSrc,
      sources,
      handleOnMouseMove,
      handleOnSelect,
    } = this.props
    const label = translate('SOURCE_CHOOSER')
    const screenReaderContent = <ScreenReaderContent>{label}</ScreenReaderContent>

    return (
      <Menu
        placement="top"
        show={showPopover}
        onToggle={togglePopover}
        trigger={
          <PlayerButton
            {...pickProps(this.props, PlayerButton.propTypes)}
            tooltipLabel={label}
            tooltipMountNode={mountNode}
            videoId={videoId}
          >
            {screenReaderContent}
            <IconSettingsSolid size="x-small" />
          </PlayerButton>
        }
        mountNode={mountNode()}
        label={label}
      >
        <MenuItemGroup
          onMouseMove={handleOnMouseMove}
          label={screenReaderContent}
          selected={[selectedSrc]}
          onSelect={handleOnSelect}
        >
          {this.renderSourcesLabels(sources)}
        </MenuItemGroup>
      </Menu>
    )
  }
}

export default SourceChooser
