import * as React from 'react';

export type OptionsRenderLabel = React.ReactNode | ((...args: any[])=>any);

export interface OptionsProps {
    /**
     * Element type to render as
     */
    as?: any;
    /**
     * The aria role of the element
     */
    role?: string;
    /**
     * The the actual list element
     */
    elementRef?: (...args: any[])=>any;
    /**
     * Content to render as a label. Mostly for when the component is nested
     */
    renderLabel?: OptionsRenderLabel;
    children?: any;
}

export class Options extends React.Component<OptionsProps, any> {
    render(): JSX.Element;

}

