import * as React from 'react';

export type PillColor = "primary" | "success" | "danger" | "info" | "warning" | "alert";

export type PillVariant = "default" | "success" | "danger" | "primary" | "warning" | "message";

export interface PillProps {
    as?: any;
    children?: React.ReactNode;
    color?: PillColor;
    elementRef?: (...args: any[])=>any;
    /**
     * Valid values are `0`, `none`, `auto`, `xxx-small`, `xx-small`, `x-small`,
     * `small`, `medium`, `large`, `x-large`, `xx-large`. Apply these values via
     * familiar CSS-like shorthand. For example: `margin="small auto large"`.
     */
    margin?: any;
    /**
     * eslint-disable react/require-default-props
     */
    text?: React.ReactNode;
    /**
     * __Deprecated - use 'color'__
     */
    variant?: PillVariant;
}

export class Pill extends React.Component<PillProps, any> {
    render(): JSX.Element;

}

