import * as React from 'react';

export type InlineSVGWidth = string | number;

export type InlineSVGHeight = string | number;

export type InlineSVGColor = "inherit" | "primary" | "secondary" | "primary-inverse" | "secondary-inverse" | "success" | "error" | "alert" | "warning" | "brand" | "auto";

export interface InlineSVGProps {
    children?: React.ReactNode;
    src?: string;
    title?: string;
    description?: string;
    focusable?: boolean;
    /**
     * To let the SVG expand to fill its container, use `auto`
     */
    width?: InlineSVGWidth;
    /**
     * To let the SVG expand to fill its container, use `auto`
     */
    height?: InlineSVGHeight;
    inline?: boolean;
    color?: InlineSVGColor;
}

export class InlineSVG extends React.Component<InlineSVGProps, any> {
    render(): JSX.Element;

}




export type SVGIconRotate = "0" | "90" | "180" | "270";

export type SVGIconSize = "x-small" | "small" | "medium" | "large" | "x-large";

export interface SVGIconProps {
    rotate?: SVGIconRotate;
    size?: SVGIconSize;
    bidirectional?: boolean;
}

export class SVGIcon extends React.Component<SVGIconProps, any> {
    render(): JSX.Element;

}

