import formatMessage from "../format-message";
const VALID_SCOPES = ["row", "col", "rowgroup", "colgroup"];
export default {
  id: "table-header-scope",
  test: elem => {
    if (elem.tagName !== "TH") {
      return true;
    }

    return VALID_SCOPES.indexOf(elem.getAttribute("scope")) !== -1;
  },
  data: elem => {
    return {
      scope: elem.getAttribute("scope") || "none"
    };
  },
  form: () => [{
    label: "Pennu ystod y pennawd",
    dataKey: "scope",
    options: [["none", "Dim"], ["row", "Rhes"], ["col", "Colofn"], ["rowgroup", "Gr\u0175p y rhes"], ["colgroup", "Gr\u0175p y golofn"]]
  }],
  update: (elem, data) => {
    if (data.header === "none") {
      elem.removeAttribute("scope");
    } else {
      elem.setAttribute("scope", data.scope);
    }

    return elem;
  },
  message: () => "Dylai penawdau tablau bennu\u2019r ystod.",
  why: () => "Ni all darllenwyr sgrin ddehongli tablau heb y strwythur priodol. Mae penawdau tablau yn nodi cyfeiriad ac ystod y cynnwys.",
  link: "https://www.w3.org/TR/WCAG20-TECHS/H63.html",
  linkText: () => "Dysgu mwy am ddefnyddio priodoleddau cwmpas gyda thablau"
};