import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "Delwedd \xE2\u2019r enw ffeil " + strings.filename(elem.src);

    case "TABLE":
      return "Tabl yn dechrau gyda " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "Dolen \xE2 thestun yn dechrau gyda " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "Paragraff yn dechrau gyda " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "Pennawd tabl yn dechrau gyda " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "Pennawd yn dechrau gyda " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "Elfen yn dechrau gyda " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}