import formatMessage from "../format-message";
const VALID_SCOPES = ["row", "col", "rowgroup", "colgroup"];
export default {
  id: "table-header-scope",
  test: elem => {
    if (elem.tagName !== "TH") {
      return true;
    }

    return VALID_SCOPES.indexOf(elem.getAttribute("scope")) !== -1;
  },
  data: elem => {
    return {
      scope: elem.getAttribute("scope") || "none"
    };
  },
  form: () => [{
    label: "Indstil overskriftens omfang",
    dataKey: "scope",
    options: [["none", "Ingen"], ["row", "R\xE6kke"], ["col", "Kolonne"], ["rowgroup", "R\xE6kkegruppe"], ["colgroup", "Kolonnegruppe"]]
  }],
  update: (elem, data) => {
    if (data.header === "none") {
      elem.removeAttribute("scope");
    } else {
      elem.setAttribute("scope", data.scope);
    }

    return elem;
  },
  message: () => "Tabeloverskrifter skal angive omfang.",
  why: () => "Sk\xE6rml\xE6sere kan ikke fortolke tabeller uden den rette struktur. Tabeloverskrifter giver en ide om indholdet og dets omfang.",
  link: "https://www.w3.org/TR/WCAG20-TECHS/H63.html",
  linkText: () => "F\xE5 mere at vide om at bruge anvendelsesomr\xE5de-attributter med tabeller"
};