import formatMessage from "../format-message";
export default {
  id: "img-alt",
  test: elem => {
    if (elem.tagName !== "IMG") {
      return true;
    }

    const alt = elem.hasAttribute("alt") ? elem.getAttribute("alt") : null;
    return alt !== null;
  },
  data: elem => {
    const alt = elem.hasAttribute("alt") ? elem.getAttribute("alt") : null;
    const decorative = alt !== null && alt.replace(/\s/g, "") === "";
    return {
      alt: alt || "",
      decorative: decorative
    };
  },
  form: () => [{
    label: "Tilf\xF8j alternativ tekst til billedet",
    dataKey: "alt",
    disabledIf: data => data.decorative
  }, {
    label: "Dekorativt billede",
    dataKey: "decorative",
    checkbox: true
  }],
  update: (elem, data) => {
    if (data.decorative) {
      elem.setAttribute("alt", "");
      elem.setAttribute("role", "presentation");
    } else {
      elem.setAttribute("alt", data.alt);
      elem.removeAttribute("role");
    }

    return elem;
  },
  message: () => "Billeder skal indeholde en alternativ attribut, der beskriver billedindholdet.",
  why: () => "Sk\xE6rml\xE6sere kan ikke bestemme, hvad der vises i et billede uden alternativ tekst, der beskriver billedets indhold og betydning.",
  link: "https://www.w3.org/TR/WCAG20-TECHS/H37.html",
  linkText: () => "F\xE5 mere at vide om at bruge alt-tekst til billeder"
};