import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "Billede med filnavn " + strings.filename(elem.src);

    case "TABLE":
      return "Tabel, der starter med " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "Link med tekst, der begynder med " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "Paragraf begynder med " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "Tabeloverskrift, der starter med " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "Overskrift, der starter med " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "Element begynder med " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}