import formatMessage from "../format-message";
import { changeTag } from "../utils/dom";
const _forEach = Array.prototype.forEach;
export default {
  id: "table-header",
  test: elem => {
    if (elem.tagName !== "TABLE") {
      return true;
    }

    return elem.querySelector("th");
  },
  data: elem => {
    return {
      header: "none"
    };
  },
  form: () => [{
    label: "Tabellen\xFCberschrift festlegen",
    dataKey: "header",
    options: [["none", "Keine Header"], ["row", "\xDCberschrift Zeile"], ["col", "\xDCberschrift Spalte"], ["both", "\xDCberschrift Zeile und Spalte"]]
  }],
  update: (elem, data) => {
    _forEach.call(elem.querySelectorAll("th"), th => {
      changeTag(th, "td");
    });

    if (data.header === "none") {
      return elem;
    }

    const row = data.header === "row" || data.header === "both";
    const col = data.header === "col" || data.header === "both";
    const tableRows = elem.querySelectorAll("tr");

    for (let i = 0; i < tableRows.length; ++i) {
      if (i === 0 && row) {
        _forEach.call(tableRows[i].querySelectorAll("td"), td => {
          const th = changeTag(td, "th");
          th.setAttribute("scope", "col");
        });

        continue;
      }

      if (!col) {
        break;
      }

      const td = tableRows[i].querySelector("td");

      if (td) {
        const th = changeTag(td, "th");
        th.setAttribute("scope", "row");
      }
    }

    return elem;
  },
  message: () => "Tabellen sollten mindestens eine \xDCberschrift haben.",
  why: () => "Bildschirmbetrachter k\xF6nnen Tabellen ohne die entsprechende Struktur nicht interpretieren. Tabellen\xFCberschriften weisen die Richtung und geben eine \xDCbersicht \xFCber den Inhalt.",
  link: "https://www.w3.org/TR/WCAG20-TECHS/H43.html",
  linkText: () => "Weitere Informationen \xFCber Tabellenkopfzeilen"
};