import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "Bild mit dem Dateinamen " + strings.filename(elem.src);

    case "TABLE":
      return "Tabelle, beginnend mit " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "Link mit Text, beginnend mit " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "Absatz, beginnend mit " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "Tabellen\xFCberschriften, beginnend mit " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "\xDCberschrift beginnt mit " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "Element beginnt mit " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}