import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "Image with filename " + strings.filename(elem.src);

    case "TABLE":
      return "Table starting with " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "Link with text starting with " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "Paragraph starting with " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "Table header starting with " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "Heading starting with " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "Element starting with " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}