import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "Imagen con nombre de archivo " + strings.filename(elem.src);

    case "TABLE":
      return "Tabla que comienza con " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "Vincular con texto que comienza con " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "P\xE1rrafo que comienza con " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "Encabezado de tabla que comienza con " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "T\xEDtulo que comienza con " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "Elemento que comienza con " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}