import formatMessage from "../format-message";
import { prepend } from "../utils/dom";
export default {
  id: "table-caption",
  test: elem => {
    if (elem.tagName !== "TABLE") {
      return true;
    }

    const caption = elem.querySelector("caption");
    return !!caption && caption.textContent.replace(/\s/g, "") !== "";
  },
  data: elem => {
    return {
      caption: ""
    };
  },
  form: () => [{
    label: "Ajoutez une l\xE9gende",
    dataKey: "caption"
  }],
  update: (elem, data) => {
    let caption = elem.querySelector("caption");

    if (!caption) {
      caption = elem.ownerDocument.createElement("caption");
      prepend(elem, caption);
    }

    caption.textContent = data.caption;
    return elem;
  },
  message: () => "Les tableaux devraient comporter une l\xE9gende d\xE9crivant le contenu du tableau.",
  why: () => "Les lecteurs d'\xE9cran ne peuvent pas interpr\xE9ter les tableaux sans la structure appropri\xE9e. Les l\xE9gendes de tableau d\xE9crivent le contexte et la compr\xE9hension g\xE9n\xE9rale du tableau.",
  link: "https://www.w3.org/TR/WCAG20-TECHS/H39.html",
  linkText: () => "En apprendre davantage sur l'utilisation des l\xE9gendes avec des tableaux"
};