import formatMessage from "../format-message";
const MAX_ALT_LENGTH = 120;
export default {
  "max-alt-length": MAX_ALT_LENGTH,
  id: "img-alt-length",
  test: elem => {
    if (elem.tagName !== "IMG") {
      return true;
    }

    const alt = elem.getAttribute("alt");
    return alt == null || alt.length <= MAX_ALT_LENGTH;
  },
  data: elem => {
    const alt = elem.getAttribute("alt");
    return {
      alt: alt || ""
    };
  },
  form: () => [{
    label: "Modifier le texte alternatif",
    dataKey: "alt",
    textarea: true
  }],
  update: (elem, data) => {
    elem.setAttribute("alt", data.alt);
    return elem;
  },
  message: () => "Le texte de l\u2019attribut alt ne devrait pas contenir plus de 120 caract\xE8res.",
  why: () => "Les lecteurs d\u2019\xE9cran ne peuvent d\xE9terminer ce qui est visible sur une image sans texte alternatif, lequel d\xE9crit le contenu et la signification de l\u2019image. Le texte alternatif doit rester simple et concis.",
  link: ""
};