import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "Image ayant le nom de fichier " + strings.filename(elem.src);

    case "TABLE":
      return "Tableau commen\xE7ant par " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "Lien contenant du texte commen\xE7ant par " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "Paragraphe commen\xE7ant par " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "En-t\xEAte de tableau commen\xE7ant par " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "En-t\xEAte commen\xE7ant par " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "\xC9l\xE9ment commen\xE7ant par " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}