import formatMessage from "../format-message";
import { prepend } from "../utils/dom";
export default {
  id: "table-caption",
  test: elem => {
    if (elem.tagName !== "TABLE") {
      return true;
    }

    const caption = elem.querySelector("caption");
    return !!caption && caption.textContent.replace(/\s/g, "") !== "";
  },
  data: elem => {
    return {
      caption: ""
    };
  },
  form: () => [{
    label: "Ajoute yon soutit",
    dataKey: "caption"
  }],
  update: (elem, data) => {
    let caption = elem.querySelector("caption");

    if (!caption) {
      caption = elem.ownerDocument.createElement("caption");
      prepend(elem, caption);
    }

    caption.textContent = data.caption;
    return elem;
  },
  message: () => "Tablo yo dwe gen lejand ki dekri kontni tablo a.",
  why: () => "Lekt\xE8 ekran yo paka ent\xE8rete tablo san estrikti ki apwopriye. Lejand tablo yo dekri kont\xE8ks ak konpreyansyon jeneral tablo a.",
  link: "https://www.w3.org/TR/WCAG20-TECHS/H39.html",
  linkText: () => "Aprann plis sou itilizasyon lejand nan tablo yo"
};