import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "Imaj ak non fichye " + strings.filename(elem.src);

    case "TABLE":
      return "Tablo k\xF2manse a " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "Lyen ak t\xE8ks ki k\xF2manse pa " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "Paragraf k\xF2manse ak " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "Ant\xE8t tablo k\xF2manse a " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "Ant\xE8t k\xF2manse pa " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "Eleman k\xF2manse a " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}