import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "Immagine con nome file " + strings.filename(elem.src);

    case "TABLE":
      return "Tabella che inizia con " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "Link al testo che inizia con " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "Paragrafo che inizia con " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "Intestazione tabella che inizia con " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "Intestazione che inizia con " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "Elemento che inizia con " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}