import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "\u30D5\u30A1\u30A4\u30EB\u540D " + strings.filename(elem.src) + " \u306E\u753B\u50CF";

    case "TABLE":
      return strings.firstWords(elem.textContent, WORD_COUNT) + " \u3067\u59CB\u307E\u308B\u8868";

    case "A":
      return strings.firstWords(elem.textContent, WORD_COUNT) + " \u3067\u59CB\u307E\u308B\u30C6\u30AD\u30B9\u30C8\u3068\u306E\u30EA\u30F3\u30AF";

    case "P":
      return strings.firstWords(elem.textContent, WORD_COUNT) + " \u3067\u59CB\u307E\u308B\u6BB5\u843D";

    case "TH":
      return strings.firstWords(elem.textContent, WORD_COUNT) + " \u3067\u59CB\u307E\u308B\u8868\u30D8\u30C3\u30C0\u30FC";

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return strings.firstWords(elem.textContent, WORD_COUNT) + " \u3067\u59CB\u307E\u308B\u898B\u51FA\u3057";

    default:
      return strings.firstWords(elem.textContent, WORD_COUNT) + " \u3067\u59CB\u307E\u308B\u8981\u7D20";
  }
}