import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "\u0100hua me te k\u014Dnae ingoa " + strings.filename(elem.src);

    case "TABLE":
      return "Ripanga e timata ana me " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "Hono me te kuputuhi e t\u012Bmata ana me " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "Parawae e t\u012Bmata ana me " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "Ka timata te pane ripanga mei te " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "Pane e t\u012Bmata ana me " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "Te t\u012Bmatanga o te kaupapa " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}