import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "Bilde med filnavn " + strings.filename(elem.src);

    case "TABLE":
      return "Tabell starter med " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "Lenke med tekst som starter med " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "Setning som starter med " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "Tabelltittel starter med " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "Tittel som starter med " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "Element som starter med " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}