import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "Plik obrazu z nazw\u0105 " + strings.filename(elem.src);

    case "TABLE":
      return "Tabela rozpoczynaj\u0105ca si\u0119 od " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "Powi\u0105\u017C z tekstem, kt\xF3ry si\u0119 zaczyna od s\u0142\xF3w " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "Akapit rozpoczynaj\u0105cy si\u0119 od " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "Nag\u0142\xF3wek tabeli rozpoczynaj\u0105cy si\u0119 od " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "Nag\u0142\xF3wek rozpoczynaj\u0105cy si\u0119 od " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "Element rozpoczynaj\u0105cy si\u0119 od " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}