import formatMessage from "../format-message";
const MAX_ALT_LENGTH = 120;
export default {
  "max-alt-length": MAX_ALT_LENGTH,
  id: "img-alt-length",
  test: elem => {
    if (elem.tagName !== "IMG") {
      return true;
    }

    const alt = elem.getAttribute("alt");
    return alt == null || alt.length <= MAX_ALT_LENGTH;
  },
  data: elem => {
    const alt = elem.getAttribute("alt");
    return {
      alt: alt || ""
    };
  },
  form: () => [{
    label: "Alterar texto alternativo",
    dataKey: "alt",
    textarea: true
  }],
  update: (elem, data) => {
    elem.setAttribute("alt", data.alt);
    return elem;
  },
  message: () => "Texto do atributo alternativo n\xE3o deve ter mais de 120 caracteres.",
  why: () => "Leitores de tela n\xE3o podem determinar o que \xE9 exibido em uma imagem sem texto alternativo, que descreve o conte\xFAdo e significado da imagem. Texto alternativo deve ser simples e conciso.",
  link: ""
};