import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "Imagem com nome de arquivo " + strings.filename(elem.src);

    case "TABLE":
      return "Tabela come\xE7ando com " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "Link com texto come\xE7ando com " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "Par\xE1grafo come\xE7ando com " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "Cabe\xE7alho de tabela come\xE7ando com " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "T\xEDtulo come\xE7ando com " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "Elemento come\xE7ando com " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}