import formatMessage from "../format-message";
import { changeTag } from "../utils/dom";
const MAX_HEADING_LENGTH = 125;
const IS_HEADING = {
  H1: true,
  H2: true,
  H3: true,
  H4: true,
  H5: true,
  H6: true
};
export default {
  "max-heading-length": MAX_HEADING_LENGTH,
  id: "paragraphs-for-headings",
  test: elem => {
    if (!IS_HEADING[elem.tagName]) {
      return true;
    }

    return elem.textContent.length <= MAX_HEADING_LENGTH;
  },
  data: elem => {
    return {
      change: false
    };
  },
  form: () => [{
    label: "Alterar a etiqueta de t\xEDtulo para o par\xE1grafo",
    checkbox: true,
    dataKey: "change"
  }],
  update: (elem, data) => {
    let ret = elem;

    if (data.change) {
      ret = changeTag(elem, "p");
    }

    return ret;
  },
  message: () => "Os t\xEDtulos n\xE3o devem conter mais de 120 caracteres.",
  why: () => "Utilizadores vistos navegam nas p\xE1ginas da Web rapidamente, procurando t\xEDtulos em grande ou em negrito. Os utilizadores do leitor de ecr\xE3 dependem de cabe\xE7alhos para a compreens\xE3o contextual. Os cabe\xE7alhos devem ser concisos dentro da estrutura adequada.",
  link: ""
};