import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "\u0418\u0437\u043E\u0431\u0440\u0430\u0436\u0435\u043D\u0438\u0435 \u0441 \u0438\u043C\u0435\u043D\u0435\u043C \u0444\u0430\u0439\u043B\u0430 " + strings.filename(elem.src);

    case "TABLE":
      return "\u0422\u0430\u0431\u043B\u0438\u0446\u0430, \u043D\u0430\u0447\u0438\u043D\u0430\u044E\u0449\u0430\u044F\u0441\u044F \u0441 " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "\u0421\u0441\u044B\u043B\u043A\u0430 \u0441 \u0442\u0435\u043A\u0441\u0442\u043E\u043C, \u043D\u0430\u0447\u0438\u043D\u0430\u044E\u0449\u0438\u043C\u0441\u044F \u0441 " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "\u041F\u0430\u0440\u0430\u0433\u0440\u0430\u0444, \u043D\u0430\u0447\u0438\u043D\u0430\u044E\u0449\u0438\u0439\u0441\u044F \u0441 " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "\u0417\u0430\u0433\u043E\u043B\u043E\u0432\u043E\u043A \u0442\u0430\u0431\u043B\u0438\u0446\u044B, \u043D\u0430\u0447\u0438\u043D\u0430\u044E\u0449\u0438\u0439\u0441\u044F \u0441 " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "\u0417\u0430\u0433\u043E\u043B\u043E\u0432\u043E\u043A, \u043D\u0430\u0447\u0438\u043D\u0430\u044E\u0449\u0438\u0439\u0441\u044F \u0441 " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "\u042D\u043B\u0435\u043C\u0435\u043D\u0442, \u043D\u0430\u0447\u0438\u043D\u0430\u044E\u0449\u0438\u0439\u0441\u044F \u0441 " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}