import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "Slika z imenom datoteke " + strings.filename(elem.src);

    case "TABLE":
      return "Preglednica, ki se za\u010Dne s/z " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "Pove\u017Ei z besedilom, ki se za\u010Dne s/z " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "Odstavek, ki se za\u010Dne s/z " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "Glava preglednice, ki se za\u010Dne s/z " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "Naslov, ki se za\u010Dne s/z " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "Element, ki se za\u010Dne s/z " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}