import formatMessage from "../format-message";
import { prepend } from "../utils/dom";
export default {
  id: "table-caption",
  test: elem => {
    if (elem.tagName !== "TABLE") {
      return true;
    }

    const caption = elem.querySelector("caption");
    return !!caption && caption.textContent.replace(/\s/g, "") !== "";
  },
  data: elem => {
    return {
      caption: ""
    };
  },
  form: () => [{
    label: "L\xE4gg till en bildtext",
    dataKey: "caption"
  }],
  update: (elem, data) => {
    let caption = elem.querySelector("caption");

    if (!caption) {
      caption = elem.ownerDocument.createElement("caption");
      prepend(elem, caption);
    }

    caption.textContent = data.caption;
    return elem;
  },
  message: () => "Tabeller ska inneh\xE5lla en bildtext som beskriver inneh\xE5llet i tabellen.",
  why: () => "Sk\xE4rml\xE4sare kan inte tolka tabeller utan r\xE4tt struktur. Tabelltexter beskriver sammanhanget och den allm\xE4nna f\xF6rst\xE5elsen av tabellen.",
  link: "https://www.w3.org/TR/WCAG20-TECHS/H39.html",
  linkText: () => "L\xE4s mer om att anv\xE4nda rubriker med tabeller"
};