import formatMessage from "../format-message";
const MAX_ALT_LENGTH = 120;
export default {
  "max-alt-length": MAX_ALT_LENGTH,
  id: "img-alt-length",
  test: elem => {
    if (elem.tagName !== "IMG") {
      return true;
    }

    const alt = elem.getAttribute("alt");
    return alt == null || alt.length <= MAX_ALT_LENGTH;
  },
  data: elem => {
    const alt = elem.getAttribute("alt");
    return {
      alt: alt || ""
    };
  },
  form: () => [{
    label: "\xC4ndra alt text",
    dataKey: "alt",
    textarea: true
  }],
  update: (elem, data) => {
    elem.setAttribute("alt", data.alt);
    return elem;
  },
  message: () => "Alt attributtext ska inte inneh\xE5lla mer \xE4n 120 tecken.",
  why: () => "Sk\xE4rml\xE4sare kan inte best\xE4mma vad som visas i en bild utan alternativ text, som beskriver inneh\xE5llet och inneb\xF6rden av bilden. Alternativ text ska vara enkel och koncis.",
  link: ""
};