import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "Bild med filnamn " + strings.filename(elem.src);

    case "TABLE":
      return "Tabell b\xF6rjar med " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "L\xE4nk med text som b\xF6rjar med " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "Stycke som b\xF6rjar med " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "Tabellrubriker b\xF6rjar med " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "Rubriker b\xF6rjar med " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "Element som b\xF6rjar med " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}